import React, { useEffect ,useRef } from "react";
import { Animated, Text, View, StyleSheet, Button , Image } from "react-native";

const App = () => {

  // USE EFFECT
  useEffect(() => {
    fadeIn()
  }, [])
  // fadeAnim will be used as the value for opacity. Initial Value: 0
  const fadeAnim = useRef(new Animated.Value(0)).current;

  const fadeIn = () => {
    // Will change fadeAnim value to 1 in 5 seconds
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 5000
    }).start();
  };

  const fadeOut = () => {
    // Will change fadeAnim value to 0 in 5 seconds
    Animated.timing(fadeAnim, {
      toValue: 0,
      duration: 5000
    }).start();
  };

  return (
    <View style={styles.container}> 

      <Animated.View
        style={[
          styles.fadingContainer,
          {
            opacity: fadeAnim // Bind opacity to animated value
          }
        ]}
      >
       <Image style={styles.fadingText} source = {require('../assets/Images/officeLogo.png')}
          style={{width:'60px',height:'60px',  marginLeft:'22px'}}
        /> 
      <Text style={styles.fadingText}>E R E S</Text>
      </Animated.View>
      <View style={styles.buttonRow}>
       {/*} <Button title="Fade In" onPress={fadeIn} />*/}
        <Button title="Fade Out" onPress={fadeOut} />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor:'black'
  },
  fadingContainer: {
    paddingVertical: 8,
    paddingHorizontal: 16,
    backgroundColor: "black"
  },
  fadingText: {
    textDecoration:'underline',
    fontSize: 28,
    fontWeight:'bold',
    textAlign: "center",
    justifyContent:"center",
    margin: 10,
    color:'#61BFB2'
  },
  buttonRow: {
    flexDirection: "row",
    marginVertical: 16
  }
});

export default App;
